mapper.select:
SELECT COUNT(*) from (select b.id from library.book b LEFT JOIN library.book_type t ON t.id=b.book_type LEFT JOIN library.book_tags s ON s.book_id=b.id LEFT JOIN library.book_tag g ON g.id=s.tag_id LEFT JOIN library.sys_user u ON u.id=b.create_by LEFT JOIN library.sys_user u2 ON u2.id=b.update_by
b.id > -1
and b.title like concat('%',#{vo.title},'%')
and b.author like concat('%',#{vo.author},'%')
and b.brand like concat('%',#{vo.brand},'%')
and b.isbn10 =#{vo.isbn10}
and b.isbn13 =#{vo.isbn13}
and b.book_type = #{vo.bookType}
and u.name like concat('%',#{vo.createName},'%')
and s.tag_id in
#{item}
group by b.id
having count(b.id) = ${vo.tagId.size()}
) as bookCount

SELECT b.id, b.title, b.sub_title, b.origin_title, b.author, t.type_name, b.price, b.total, b.current_number, b.likes, b.borrows, u.`name` createName, u2.`name` updateName FROM library.book b LEFT JOIN library.book_type t ON t.id=b.book_type LEFT JOIN library.attachment a on a.id = b.attachment_id LEFT JOIN library.book_tags s ON s.book_id=b.id LEFT JOIN library.book_tag g ON g.id=s.tag_id LEFT JOIN library.sys_user u ON u.id=b.create_by LEFT JOIN library.sys_user u2 ON u2.id=b.update_by
and b.title like concat('%',#{vo.title},'%')
and b.author like concat('%',#{vo.author},'%')
and b.brand like concat('%',#{vo.brand},'%')
and b.isbn10 =#{vo.isbn10}
and b.isbn13 =#{vo.isbn13}
and b.book_type = #{vo.bookType}
and u.name like concat('%',#{vo.createName},'%')
and s.tag_id in
#{item}
group by b.id
having count(b.id) = ${vo.tagId.size()}
order by b.id desc limit #{ew.start}, #{ew.size}

SELECT b.id, b.title, b.sub_title, b.origin_title, b.author, b.publisher, b.pubdate, t.type_name, a.img_url, a.img_desc, a.data_href, b.price, b.total, b.current_number, b.likes, b.borrows FROM library.book b LEFT JOIN library.book_type t ON t.id=b.book_type LEFT JOIN library.attachment a on a.id = b.attachment_id LEFT JOIN library.book_tags s ON s.book_id=b.id LEFT JOIN library.book_tag g ON g.id=s.tag_id LEFT JOIN library.sys_user u ON u.id=b.create_by LEFT JOIN library.sys_user u2 ON u2.id=b.update_by
and b.title like concat('%',#{vo.title},'%')
and b.author like concat('%',#{vo.author},'%')
and b.brand like concat('%',#{vo.brand},'%')
and b.isbn10 =#{vo.isbn10}
and b.isbn13 =#{vo.isbn13}
and b.book_type = #{vo.bookType}
and u.name like concat('%',#{vo.createName},'%')
and s.tag_id in
#{item}
group by b.id
having count(b.id) = ${vo.tagId.size()}
order by b.id desc limit #{ew.start}, #{ew.size}

select
id, title, sub_title, origin_title, author, book_type, bindng, pages, attachment_id, isbn10, isbn13, summary,
original_texts, price, total, current_number, update_by, create_by, update_time, create_time, publisher,
pubdate, likes
from library.book


        and id = #{id}


        and title = #{title}


        and sub_title = #{subTitle}


        and origin_title = #{originTitle}


        and author = #{author}


        and book_type = #{bookType}


        and bindng = #{bindng}


        and pages = #{pages}


        and attachment_id = #{attachmentId}


        and isbn10 = #{isbn10}


        and isbn13 = #{isbn13}


        and summary = #{summary}


        and original_texts = #{originalTexts}


        and price = #{price}


        and total = #{total}


        and current_number = #{currentNumber}


        and update_by = #{updateBy}


        and create_by = #{createBy}


        and update_time = #{updateTime}


        and create_time = #{createTime}


        and publisher = #{publisher}


        and pubdate = #{pubdate}


        and likes = #{likes}
